""" PR Manager Agent - Creates and manages GitHub pull requests """
from google.adk.agents import LlmAgent from google.adk.tools import Tool from typing import Dict, List, Any, Optional from datetime import datetime import json import logging
logger = logging.getLogger(__name__)
def create_github_pr_tool() -> Tool: """ Create a tool for creating GitHub PRs using MCP """ async def create_pull_request(mcp_client, title: str, body: str, branch: str, labels: List[str]) -> Dict: """Create a GitHub pull request""" try: # Create PR using GitHub MCP result = await mcp_client.call_tool( server="github", tool="create_pull_request", arguments={ "owner": "fabio-lpd", "repo": "lpd-claude-code-monitor", "title": title, "body": body, "head": branch, "base": "main", "draft": False, "maintainer_can_modify": True } )
if result and 'number' in result: pr_number = result['number']
# Add labels if labels: await mcp_client.call_tool( server="github", tool="add_labels", arguments={ "owner": "fabio-lpd", "repo": "lpd-claude-code-monitor", "issue_number": pr_number, "labels": labels } )
return { 'success': True, 'pr_number': pr_number, 'pr_url': result.get('html_url'), 'title': title }
return {'success': False, 'error': 'Failed to create PR'}
except Exception as e: logger.error(f"Error creating PR: {e}") return {'success': False, 'error': str(e)}
return Tool( name="create_pull_request", description="Create a GitHub pull request", function=create_pull_request )
def create_pr_status_tool() -> Tool: """ Create a tool for checking PR status """ async def check_pr_status(mcp_client, pr_number: int) -> Dict: """Check the status of a pull request""" try: result = await mcp_client.call_tool( server="github", tool="get_pull_request", arguments={ "owner": "fabio-lpd", "repo": "lpd-claude-code-monitor", "pullNumber": pr_number } )
if result: return { 'state': result.get('state'), 'merged': result.get('merged', False), 'mergeable': result.get('mergeable'), 'reviews': result.get('reviews', []), 'checks': result.get('status_checks', {}) }
return {'error': 'Could not get PR status'}
except Exception as e: logger.error(f"Error checking PR status: {e}") return {'error': str(e)}
return Tool( name="check_pr_status", description="Check the status of a pull request", function=check_pr_status )
def create_pr_manager_agent() -> LlmAgent: """ Create the PR Manager agent """
pr_manager = LlmAgent( name="pr_manager", model="gemini-2.0-flash", description="Creates and manages GitHub pull requests", instruction=""" You are the PR Manager Agent for GitHub operations.
CONTEXT: - Repository: fabio-lpd/lpd-claude-code-monitor - Default branch: main - Environment: PRODUCTION
YOUR RESPONSIBILITIES:
1. CREATE PULL REQUESTS:
