def get_investigation_summary(self): """Get summary of all investigations""" print("\nðŸ“ˆ INVESTIGATION SUMMARY") print("=" * 70)
# Clean up old sessions current_time = datetime.now() active_sessions = 0 completed_sessions = 0
for pid, session in list(self.sessions.items()): if isinstance(session['last_seen'], str): session['last_seen'] = datetime.fromisoformat(session['last_seen'])
time_since_seen = current_time - session['last_seen']
if time_since_seen.seconds < 60: # Seen in last minute active_sessions += 1 else: completed_sessions += 1 session['status'] = 'completed'
print(f"ðŸ“Š Statistics:") print(f" Active Sessions: {active_sessions}") print(f" Completed Today: {completed_sessions}") print(f" Total Tracked: {len(self.sessions)}")
# Show recent completions if completed_sessions > 0: print(f"\n Recent Completions:") for pid, session in self.sessions.items(): if session.get('status') == 'completed': print(f" â€¢ {session.get('queue', 'Unknown')} - PID {pid}")
def format_duration(self, duration): """Format duration in human-readable format""" if isinstance(duration, timedelta): total_seconds = int(duration.total_seconds()) else: total_seconds = int(duration)
hours = total_seconds // 3600 minutes = (total_seconds % 3600) // 60 seconds = total_seconds % 60
if hours > 0: return f"{hours}h {minutes}m {seconds}s" elif minutes > 0: return f"{minutes}m {seconds}s" else: return f"{seconds}s"
